import React from 'react';
import { ChannelList,useChatContext } from 'stream-chat-react';
import Cookies from 'universal-cookie';
import { ChannelSearch, TeamChannelList, TeamChannelPreview } from '../component';
import ChatIcon from '../assets/chaticon.png'
import LogoutIcon from '../assets/logout.png';


const cookies = new Cookies;


const Sidebar=({ logout })=>(
    <div className="channel-list__sidebar">
        <div className="channel-list__sidebar__icon1">
            <div className="icon1__inner">
                <img src={ChatIcon} alt="Chat" width="30"/>

            </div>
        </div>

        <div className="channel-list__sidebar__icon2">
            <div className="icon1__inner"  onClick={logout}>
                <img src={LogoutIcon} alt="Logout" width="30"/>

            </div>

        </div>

    </div>
);

const CompanyHeader = ()=>(
    <div className="channel-list__header">
        <p className="channel-list__header__text">Collaborator</p>

    </div>
)

const customChannelTeamfilter=(channelIs)=>{
    return channelIs.filter((channel)=> channel.type === 'team');
}

const customChannelMessageFilter=(channelIs)=>{
    return channelIs.filter((channel)=>channel.type === 'messaging');
}


const ChannelListContainer= ({ isCreating ,setIsCreating,setCreateType,setIsEditing})=>{

    const logout = ()=>{
        
        const authToken = cookies.get("token");
        cookies.remove("token");
        cookies.remove('userId');
        cookies.remove('username');
        cookies.remove('fullName');
        cookies.remove('avatarURL');
        cookies.remove('hashedPassword');
        cookies.remove('phoneNumber');

        window.location.reload();


    }

    return (
        <>
            <Sidebar logout={logout}/>
            <div className="channel-list__list__wrapper">
                <CompanyHeader/>
                <ChannelSearch/>
                <ChannelList 
                filter={{}}
                channelRenderFilterFn={customChannelTeamfilter}
                List={(listProps)=>(
                    <TeamChannelList
                        {...listProps}
                        type="team"

                        isCreating = { isCreating }
                        setIsCreating = {setIsCreating}
                        setCreateType = { setCreateType }
                        setIsEditing = {setIsEditing}




                        />

                )}
                Preview={(previewprops)=>(
                    <TeamChannelPreview
                    {...previewprops}
                    type="team"
                    />
                )}

                />

                <ChannelList 
                filter={{}}
                channelRenderFilterFn={customChannelMessageFilter}
                List={(listProps)=>(
                    <TeamChannelList
                        {...listProps}
                        type="messaging"

                        isCreating = { isCreating }
                        setIsCreating = {setIsCreating}
                        setCreateType = { setCreateType }
                        setIsEditing = {setIsEditing}


                        />

                )}
                Preview={(previewprops)=>(
                    <TeamChannelPreview
                    {...previewprops}
                    type="messaging"
                    />
                )}

                />


            </div>
        </>
    )
}

export default ChannelListContainer;
